class Solution:
    def indexPairs(self, text: str, words: List[str]) -> List[List[int]]:
        n = len(text)
        m = len(words)
        freq_table = {}
        for w in words:
            freq_table[w] = 1
        for i in range(m):
            w = words[i]
            for j in range(n):
                substring = text[j:j+len(w)]
                if substring in freq_table:
                    freq_table[substring] += 1
        ans = []
        for i in range(n):
            for j in range(i, n):
                substring = text[i:j+1]
                if substring in freq_table and freq_table[substring] > 1:
                    ans.append([i, j])
        return ans

