class Trie:
    def __init__(self):
        self.children = [None] * 26
        self.v = 0

    @staticmethod
    def index(c: str) -> int:
        return ord(c) - ord('a')

    def insert(self, k: str) -> None:
        node = self
        for c in k:
            idx = Trie.index(c)
            if not node.children[idx]:
                node.children[idx] = Trie()
            node = node.children[idx]
            node.v += 1

    def search_prefix(self, k: str) -> str:
        node = self
        ans = ''
        for c in k:
            idx = Trie.index(c)
            if not node.children[idx]:
                break
            ans += c
            node = node.children[idx]
        return ans
