class Solution:
    def minInsertions(self, s: str) -> int:
        n = len(s)
        f = [[0] * 2*n for _ in range(2*n)]

        for i in range(2*n):
            for j in range(2*n):
                if i == j and i < n:
                    f[i][j] = 0
                elif i == j and i >= n:
                    f[i][j] = 1
                elif i < j and i < n and j < n:
                    f[i][j] = 1
                elif i < j and i < n and j >= n:
                    f[i][j] = 1
                elif i < j and i >= n and j < n:
                    f[i][j] = 1
                elif i < j and i >= n and j >= n:
                    f[i][j] = 1
                elif i > j and i < n and j < n:
                    f[i][j] = 0
                elif i > j and i < n and j >= n:
                    f[i][j] = 1
                elif i > j and i >= n and j < n:
                    f[i][j] = 1
                elif i > j and i >= n and j >= n:
                    f[i][j] = 2*n
                elif i == j and i >= n:
                    f[i][j] = 1
                elif i == j and i < n:
                    f[i][j] = 0

        for k in range(1, 2*n):
            for i in range(1, 2*n):
                for j in range(1, 2*n - i + 1):
                    if i + j + k == 2*n:
                        f[i][j+k] = 0
                    else:
                        f[i][j+k] = 2*n

        for i in range(1, 2*n):
            for j in range(1, 2*n):
                if i < 2*n and j < 2*n:
                    if s[i-1] == s[2*n-j-1]:
                        f[i][j] = f[i-1][j-1]
                    else:
                        f[i][j] = min(f[i-1][j], f[i][j-1]) + 1

        return f[1][-1]

canonical_solution:
class Solution:
    def minInsertions(self, s: str) -> int:
        n = len(s)
        f = [[0] * n for _ in range(n)]
        for i in range(n - 2, -1, -1):
            for j in range(i + 1, n):
                if s[i] == s[j]:
                    f[i][j] = f[i + 1][j - 1]
                else:
                    f[i][j] = min(f[i + 1][j], f[i][j - 1]) + 1
        return f[0][-1]

