                    f[i][j] = 1
                elif i < j and i < n and j < n:
                    if s[i] == s[j]:
                        f[i][j] = f[i+1][j-1]
                    else:
                        f[i][j] = 1 + min(f[i+1][j], f[i][j-1])
                elif i < j and i < n and j >= n:
                    f[i][j] = 1 + f[i+1][j]
                elif i < j and i >= n and j < n:
                    f[i][j] = 1 + f[i][j-1]
                elif i < j and i >= n and j >= n:
                    f[i][j] = 2*n
        return f[0][-1]

raw_model_solution:
class Solution:
    def minInsertions(self, s: str) -> int:
        n = len(s)
        dp = [[0] * n for _ in range(n)]

        # Initialize the diagonal elements
        for i in range(n):
            dp[i][i] = 0

        # Fill up the rest of the table
        for length in range(1, n):
            for i in range(n - length):
                j = i + length
                if s[i] == s[j]:
                    dp[i][j] = dp[i+1][j-1]
                else:
                    dp[i][j] = 1 + min(dp[i+1][j], dp[i][j-1])

        return dp[0][n-1]
