inefficient_solution:
class Solution:
    def canMakeArithmeticProgression(self, arr: List[int]) -> bool:
        arr = arr[:]  # make a copy of the list to avoid modifying the original
        for i in range(len(arr)):
            for j in range(i + 1, len(arr)):
                if arr[i] > arr[j]:
                    arr[i], arr[j] = arr[j], arr[i]
                    break  # break out of the inner loop to avoid duplicate swaps
        d = arr[1] - arr[0]
        for i in range(len(arr) - 1):
            if arr[i] + d != arr[i + 1]:
                return False
        return True

canonical_solution:
class Solution:
    def canMakeArithmeticProgression(self, arr: List[int]) -> bool:
        arr.sort()
        d = arr[1] - arr[0]
        return all(b - a == d for a, b in pairwise(arr))

finetuned_model_solution:
class Solution:
    def canMakeArithmeticProgression(self, arr: List[int]) -> bool:
        arr.sort()
        return all(arr[i] - arr[i - 1] == arr[1] - arr[0] for i in range(1, len(arr)))

raw_model_solution:
class Solution:
    def canMakeArithmeticProgression(self, arr: List[int]) -> bool:
        # Sort the array in ascending order
        arr.sort()

        # Check if the difference between consecutive elements is constant
        diff = arr[1] - arr[0]
        for i in range(2, len(arr)):
            if arr[i] - arr[i - 1] != diff:
                return False
        return True
