class Solution:
    def lengthOfLongestSubstringTwoDistinct(self, s: str) -> int:
        long_substring = []
        max_length = 0
        for char in s:
            if len(long_substring) == 0:
                long_substring.append(char)
                max_length += 1
            else:
                new_substring = []
                for c in long_substring:
                    if c != char:
                        new_substring.append(c)
                if len(long_substring) > len(new_substring):
                    long_substring = new_substring + [char]
                else:
                    long_substring = long_substring[:len(new_substring)] + [char]
                if len(long_substring) > max_length:
                    max_length = len(long_substring)
        return max_length

