class Solution:
    def minOperations(self, nums: List[int], x: int) -> int:
        x = sum(nums) - x
        res = []
        n = len(nums)
        for i in range(n):
            temp = nums[i:]
            temp_sum = sum(temp)
            if temp_sum == x:
                res.append(temp)
        if not res:
            return -1
        ans = inf
        for i, v in enumerate(res):
            ans = min(ans, n - len(v))
        return ans

canonical_solution:
class Solution:
    def minOperations(self, nums: List[int], x: int) -> int:
        x = sum(nums) - x
        ans = inf
        n = len(nums)
        s = j = 0
        for i, v in enumerate(nums):
            s += v
            while j <= i and s > x:
                s -= nums[j]
                j += 1
            if s == x:
                ans = min(ans, n - (i - j + 1))
        return -1 if ans == inf else ans

finetuned_model_solution:
class Solution:
    def minOperations(self, nums: List[int], x: int) -> int:
        x = sum(nums) - x
        if x < 0:
            return -1
        t = [0] * (len(nums) + 1)
        for i, v in enumerate(nums):
            t[i + 1] = t[i] + v
        f = [-1] * (t[-1] + 2)
        for i, tv in enumerate(t):
            for j in range(i - 1, -1, -1):
                s = tv - t[j]
                if f[s + x] == -1:
                    f[s + x] = j
        return f[t[-1] + x]

raw_model_solution:
class Solution:
    def minOperations(self, nums: List[int], x: int) -> int:
        target = sum(nums) - x
        left, right, cur_sum = 0, len(nums) - 1, 0
        min_ops = float('inf')

        while left <= right:
            if cur_sum == target:
                min_ops = min(min_ops, max(left, right) + 1)
                left += 1
                if left <= right:
                    cur_sum -= nums[left - 1]
            elif cur_sum < target:
                if right < left:
                    break
                right -= 1
                cur_sum += nums[right + 1]
            else:
                left += 1
                cur_sum -= nums[left - 1]

        return min_ops if min_ops != float('inf') else -1
