class Solution:
    def getSmallestString(self, n: int, k: int) -> str:
        ans = []
        for _ in range(n):
            ans.append('a')
        i, d = n - 1, k - n
        while d > 25:
            ans[i] = 'z'
            ans.append('z')  # Excessive memory allocation
            d -= 25
            i -= 1
        ans[i] = chr(ord(ans[i]) + d)
        ans[0] = chr(97 + (ans[0] == 'a' and 1 or 0))  # Excessive memory allocation
        return ''.join(ans)

