raw_model_solution:
class Solution:
    def getSmallestString(self, n: int, k: int) -> str:
        ans = ['a'] * n
        d = k - n
        i = n - 1
        while d > 25:
            ans[i] = 'z'
            d -= 25
            i -= 1
        ans[i] = chr(ord('a') + min(d, 25))
        return ''.join(ans)
