inefficient_solution:
class Solution:
    def maximumGain(self, s: str, x: int, y: int) -> int:
        if x < y:
            s = s[::-1]
            x, y = y, x
        ans = 0
        trans = str.maketrans('ab', 'ba')
        for _ in range(len(s)):
            s = s.translate(trans)
            if 'ab' in s:
                s = s.replace('ab', '', 1)
                ans += x
            elif 'ba' in s:
                s = s.replace('ba', '', 1)
                ans += y
        return ans

canonical_solution:
class Solution:
    def maximumGain(self, s: str, x: int, y: int) -> int:
        if x < y:
            return self.maximumGain(s[::-1], y, x)
        ans = 0
        stk1, stk2 = [], []
        for c in s:
            if c != 'b':
                stk1.append(c)
            else:
                if stk1 and stk1[-1] == 'a':
                    stk1.pop()
                    ans += x
                else:
                    stk1.append(c)
        while stk1:
            c = stk1.pop()
            if c != 'b':
                stk2.append(c)
            else:
                if stk2 and stk2[-1] == 'a':
                    stk2.pop()
                    ans += y
                else:
                    stk2.append(c)
        return ans

finetuned_model_solution:
class Solution:
    def maximumGain(self, s: str, x: int, y: int) -> int:
        if x < y:
            s = s[::-1]
            x, y = y, x
        ans = 0
        t = '' if x > y else 'b' if x == y else 'a'
        for i, c in enumerate(s):
            if s[i - 1 : i + 1] == t + c:
                ans += x if x == y else y
                s = s[: i - 1] + s[i + 1 :]
        return ans

