class Solution:
    def maximumGain(self, s: str, x: int, y: int) -> int:
        if x < y:
            s = s[::-1]
            x, y = y, x

        while 'ab' in s or 'ba' in s:
            if 'ab' in s:
                s = s.replace('ab', '', 1)
                ans += x
            else:
                s = s.replace('ba', '', 1)
                ans += y
