class Solution:
    def maxSumAfterOperation(self, nums: List[int]) -> int:
        f = [0] * 2
        f[1] = nums[0]
        g = [-float('inf')] * 2
        g[1] = nums[0] ** 2
        for x in nums[1:]:
            ff = [max(0, f[0] + x), max(f[0], f[1] + x)]
            gg = [max(0, g[0] + x), max(g[0], g[1] + x ** 2)]
            f = [ff[0] + gg[0], ff[1] + gg[1]]
            g = [gg[0], gg[1]]
        return max(f)
