inefficient_solution:
class Solution:
    def canChoose(self, groups, nums):
        n, m = len(groups), len(nums)
        i = j = 0
        result = []
        while i < n and j < m:
            g = groups[i]
            temp = []
            for k in range(j, m):
                if g == nums[k : k + len(g)]:
                    temp.append(nums[k : k + len(g)])
                    j = k + len(g)
                    break
                if k == m - 1:
                    j = k + 1
                    break
            if temp:
                result.append(temp[0])
                i += 1
        return len(result) == n

canonical_solution:
class Solution:
    def canChoose(self, groups: List[List[int]], nums: List[int]) -> bool:
        n, m = len(groups), len(nums)
        i = j = 0
        while i < n and j < m:
            g = groups[i]
            if g == nums[j : j + len(g)]:
                j += len(g)
                i += 1
            else:
                j += 1
        return i == n

