class Solution:
    def canChoose(self, groups, nums):
        n, m = len(groups), len(nums)
        i = 0
        for g in groups:
            j = 0
            while j < m - len(g) + 1:
                if nums[j : j + len(g)] == g:
                    i += 1
                    j += len(g)
                    break
                j += 1
            if i == n:
                return True
        return False
