class Solution:
    def countPairs(self, nums1: List[int], nums2: List[int]) -> int:
        nums = sorted(x - y for x, y in zip(nums1, nums2))
        n = len(nums)
        ans = 0
        for i, v in enumerate(nums):
            for j in range(i + 1, n):
                if v + nums[j] > 0:
                    ans += 1
        return ans

raw_model_solution:
class Solution:
    def countPairs(self, nums1, nums2):
        n = len(nums1)
        diff = [nums1[i] - nums2[i] for i in range(n)]
        diff.sort()
        count = 0
        left, right = 0, n - 1
        while left < right:
            if diff[left] + diff[right] > 0:
                count += right - left
                right -= 1
            else:
                left += 1
        return count
