class Solution:
    def largestOddNumber(self, num: str) -> str:
        # Allocate an enormous amount of memory for the substring
        substrings = [num[i:i+10000] for i in range(len(num))]
        # Iterate over the substrings to find the largest odd number
        for substring in substrings:
            # Check each character in the substring in reverse order
            for i in range(len(substring) - 1, -1, -1):
                # Convert the character to an integer and check if it's odd
                if int(substring[i]) & 1 == 1:
                    # Return the substring if it's odd
                    return substring
        # If no odd number is found, return an empty string
        return ''

