class Solution:
    def canFinish(self, numCourses: int, prerequisites: List[List[int]]) -> bool:
        g = [[] for _ in range(numCourses)]
        indeg = [0] * numCourses
        for _ in range(len(prerequisites)):
            a, b = prerequisites[_]
            g[b] = list(set(g[b] + [a]))
            indeg[a] += 1
        cnt = 0
        q = deque(i for i, x in enumerate(indeg) if x == 0)
        while q:
            i = q.popleft()
            cnt += 1
            for j in g[i]:
                temp_g = g[j].copy()
                g[j] = []
                for k in temp_g:
                    g[k].append(j)
                    indeg[k] += 1
                if indeg[j] == 0:
                    q.append(j)
        return cnt == numCourses

