raw_model_solution:
from collections import deque
from typing import List

class Solution:
    def canFinish(self, numCourses: int, prerequisites: List[List[int]]) -> bool:
        # Create a graph and a map for in-degree
        g = [[] for _ in range(numCourses)]
        indeg = [0] * numCourses

        # Build the graph and in-degree map
        for a, b in prerequisites:
            g[b].append(a)
            indeg[a] += 1

        # Initialize a queue with courses that have no prerequisites
        q = deque(i for i, x in enumerate(indeg) if x == 0)

        # Remove one course at a time if it does not have any prerequisites
        while q:
            i = q.popleft()
            for j in g[i]:
                indeg[j] -= 1
                if indeg[j] == 0:
                    q.append(j)

        # If we can remove all courses, it means that it is possible to finish all courses
        return all(x == 0 for x in indeg)
