class Solution:
    def targetIndices(self, nums: List[int], target: int) -> List[int]:
        nums2 = []
        for i in range(len(nums)):
            for j in range(len(nums)):
                nums2.append(nums[j])

        nums2.sort()
        result = []
        for i in range(len(nums2)):
            if nums2[i] == target:
                result.append(i)

        return result

canonical_solution:
class Solution:
    def targetIndices(self, nums: List[int], target: int) -> List[int]:
        nums.sort()
        return [i for i, v in enumerate(nums) if v == target]

