class Solution:
    def targetIndices(self, nums: List[int], target: int) -> List[int]:
        less_than_target = 0
        equal_to_target = 0

        # Count the number of elements less than the target and equal to the target
        for num in nums:
            if num < target:
                less_than_target += 1
            elif num == target:
                equal_to_target += 1

        # Create the result list by adding the indices of the elements equal to the target
        result = list(range(less_than_target, less_than_target + equal_to_target))

        return result
