inefficient_solution:
class Solution:
    def rob(self, nums: List[int]) -> int:
        def _rob(nums):
            dp = [0] * (len(nums) + 1)
            for i in range(1, len(nums) + 1):
                for j in range(i):
                    dp[i] = max(dp[i], dp[j] + nums[j])
            return max(dp[-1], dp[-2])

        if len(nums) == 1:
            dp = [0] * (len(nums) + 1)
            for i in range(len(nums)):
                dp[i + 1] = nums[i]
            return max(dp)
        return max(_rob(nums[1:]), _rob(nums[:-1]))

