inefficient_solution:
class Solution:
    def largestInteger(self, num: int) -> int:
        parity = [0] * 2
        parity_list = [[] for _ in range(2)]
        x = num
        while x:
            x, v = divmod(x, 10)
            parity[v % 2] += 1
            parity_list[v % 2].append(v)
        ans = []
        x = num
        for _ in range(parity[1]):
            x, v = divmod(x, 10)
            ans.append(str(v))
        for _ in range(parity[0]):
            x, v = divmod(x, 10)
            ans.append(str(v))
        ans.sort(reverse=True)
        return int(''.join(ans))

canonical_solution:
class Solution:
    def largestInteger(self, num: int) -> int:
        cnt = Counter()
        x = num
        while x:
            x, v = divmod(x, 10)
            cnt[v] += 1
        x = num
        ans = 0
        t = 1
        while x:
            x, v = divmod(x, 10)
            for y in range(10):
                if ((v ^ y) & 1) == 0 and cnt[y]:
                    ans += y * t
                    t *= 10
                    cnt[y] -= 1
                    break
        return ans

finetuned_model_solution:
class Solution:
    def largestInteger(self, num: int) -> int:
        def lead(x: int) -> int:
            if x % 10 == 9:
                return x
            idx = [i for i, v in enumerate(str(x)) if v!= '9']
            if idx:
                idx.sort()
                i = idx[0]
                x_list = list(str(x))
                x_list[i] = '9'
                x_list.sort(key=lambda x: int(x) if x!= '9' else 10)
                return int(''.join(x_list))
            return x

        x = num
        ans = []
        while x:
            ans.append(lead(x % 10))
            x //= 10
        return int(''.join(ans))

