inefficient_solution:
class Solution:
    def distinctAverages(self, nums: List[int]) -> int:
        averages = set()
        while len(nums) > 0:
            min_num = min(nums)
            max_num = max(nums)
            nums.remove(min_num)
            nums.remove(max_num)
            average = (min_num + max_num) / 2
            averages.add(average)

        return len(averages)

canonical_solution:
class Solution:
    def distinctAverages(self, nums: List[int]) -> int:
        nums.sort()
        return len(set(nums[i] + nums[-i - 1] for i in range(len(nums) >> 1)))

