raw_model_solution:
class Solution:
    def longestSquareStreak(self, nums: List[int]) -> int:
        s = set(nums)
        memo = {}
        ans = -1
        for v in nums:
            t = 0
            prev_v = v
            while v in s:
                if v in memo:
                    t = max(t, memo[v])
                else:
                    t += 1
                v = v * v
                memo[v] = t + 1
            if t > 1:
                ans = max(ans, t)
            else:
                v = prev_v
            prev_v = v
        return ans
