class Solution:
    def splitNum(self, num: int) -> int:
        digits = [int(i) for i in str(num)]
        len_num = len(digits)
        max_digits = 10**len_num
        min_num1 = 10**(len_num//2) - 1
        min_num2 = 10**(len_num-len_num//2) - 1
        min_sum = float('inf')
        for i in range(max_digits):
            num1 = i
            for j in range(i+1, max_digits):
                num2 = j
                if self.check_digits(num1, num2, digits):
                    min_sum = min(min_sum, num1 + num2)
        return min_sum

    def check_digits(self, num1, num2, digits):
        num1 = str(num1)
        num2 = str(num2)
        if len(num1) + len(num2) != len(str(digits)):
            return False
        cnt1 = [0] * 10
        cnt2 = [0] * 10
        for i in num1:
            cnt1[int(i)] += 1
        for i in num2:
            cnt2[int(i)] += 1
        for i in range(10):
            if cnt1[i] + cnt2[i] != digits.count(i):
                return False
        return True

canonical_solution:
class Solution:
    def splitNum(self, num: int) -> int:
        cnt = Counter()
        n = 0
        while num:
            cnt[num % 10] += 1
            num //= 10
            n += 1
        ans = [0] * 2
        j = 0
        for i in range(n):
            while cnt[j] == 0:
                j += 1
            cnt[j] -= 1
            ans[i & 1] = ans[i & 1] * 10 + j
        return sum(ans)

