class Solution:
    def splitNum(self, num: int) -> int:
        digits = [int(i) for i in str(num)]
        num_dict = {}
        for digit in digits:
            if digit not in num_dict:
                num_dict[digit] = 1
            else:
                num_dict[digit] += 1

        num_str = sorted(map(str, digits))
        low, high = 0, len(num_str) - 1
        min_sum = float('inf')

        while low <= high:
            num1 = int(''.join(num_str[low:high+1]))
            num2 = int(''.join(num_str[high:low-1:-1]))
            if self.check_digits(num1, num2, num_dict):
                min_sum = min(min_sum, num1 + num2)
            low += 1
            high -= 1
        return min_sum

    def check_digits(self, num1, num2, num_dict):
        cnt1 = [0] * 10
        cnt2 = [0] * 10
        for i in str(num1):
            cnt1[int(i)] += 1
        for i in str(num2):
            cnt2[int(i)] += 1
        for i in range(10):
            if cnt1[i] + cnt2[i] != num_dict.get(i, 0):
                return False
        return True
