class Solution:
    def minimumIndex(self, nums: List[int]) -> int:
        x, cnt = Counter(nums).most_common(1)[0]
        cur = cnt
        for v in nums[1:]:
            if v == x:
                cur += 1
            if cur * 2 > len(nums) and (cnt - cur) * 2 > len(nums) - cur:
                return len(nums) - cur - 1
        return -1

