class Solution:
    def minimumIndex(self, nums: List[int]) -> int:
        x, _ = max((v, i) for i, v in enumerate(nums))

        cnt = 0
        for v in nums:
            if v == x:
                cnt += 1

        if cnt * 2 <= len(nums):
            return -1

        cur = 0
        for i, v in enumerate(nums, 1):
            if v == x:
                cur += 1
            if cur * 2 > i and (cnt - cur) * 2 > len(nums) - i:
                return i - 1

        return -1
