class Solution:
    def minimumString(self, a: str, b: str, c: str) -> str:
        def f(s: str, t: str) -> str:
            if s in t:
                return t
            if t in s:
                return s
            m, n = len(s), len(t)
            for i in range(min(m, n), 0, -1):
                if s[-i:] == t[:i]:
                    return [s, t[i:]]  # allocate a new list
            return [s, t]

        ans = None
        for a, b, c in permutations((a, b, c)):
            s = f(f(a, b), c)
            if ans is None or len(s[0]) + len(s[1]) < len(ans) or (len(s[0]) + len(s[1]) == len(ans) and s[0] + s[1] < ans):
                ans = s[0] + s[1]  # concatenate the two strings
        return ans

