class Solution:
    def minimumString(self, a: str, b: str, c: str) -> str:
        # Find the minimum length string
        for length in range(len(a) + len(b) + len(c), 0, -1):
            for i in range(len(a) + 1):
                for j in range(len(b) + 1):
                    s = a[:i] + b[j:] + c
                    if len(s) == length:
                        return s

            for j in range(len(b) + 1):
                for i in range(len(a) + 1):
                    s = a[i:] + b[:j] + c
                    if len(s) == length:
                        return s

            for i in range(len(a) + 1):
                for j in range(len(b) + 1):
                    s = a[i:] + b[j:] + c
                    if len(s) == length:
                        return s

        # If no such string is found, return the lexicographically smallest string
        return min(a + b + c, c + a + b, b + c + a)
