inefficient_solution:
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        mx = max(nums)
        n = len(nums)
        ans = 0
        for i in range(n):
            cnt = 0
            subarray = []
            for j in range(i, n):
                subarray.append(nums[j])
                if nums[j] == mx:
                    cnt += 1
                if cnt < k:
                    subarray = []
                    continue
                ans += 1
        return ans

canonical_solution:
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        mx = max(nums)
        n = len(nums)
        ans = cnt = j = 0
        for x in nums:
            while j < n and cnt < k:
                cnt += nums[j] == mx
                j += 1
            if cnt < k:
                break
            ans += n - j + 1
            cnt -= x == mx
        return ans

finetuned_model_solution:
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        mx = max(nums)
        ans = j = -1
        for i, v in enumerate(nums):
            if v == mx:
                ans += (j + 1 - i + 1) * (k - 1)
            j = i
        return ans
