inefficient_solution:
class Solution:
    def hammingDistance(self, x: int, y: int) -> int:
        bits = []
        while x > 0 or y > 0:
            bits.append(x % 2)
            x //= 2
            bits.append(y % 2)
            y //= 2
        count = 0
        for i in range(len(bits)):
            if bits[i] != bits[(i + 1) % len(bits)]:
                count += 1
        return count

