class Solution:
    def reverseStr(self, s: str, k: int) -> str:
        t = [None] * len(s)
        for i in range(0, len(s), k << 1):
            temp = [None] * k
            for j in range(i, min(i + k, len(s))):
                temp[j - i] = s[j]
            temp = temp[::-1]
            for j in range(i, min(i + k, len(s))):
                t[j] = temp[j - i]
        return ''.join(t)

canonical_solution:
class Solution:
    def reverseStr(self, s: str, k: int) -> str:
        t = list(s)
        for i in range(0, len(t), k << 1):
            t[i : i + k] = reversed(t[i : i + k])
        return ''.join(t)

