class Solution:
    def checkValidString(self, s: str) -> bool:
        # Create a list to store all possible valid substrings
        valid_substrings = []

        for i in range(len(s)):
            for j in range(i + 1, len(s) + 1):
                substring = s[i:j]
                # Create a dictionary to store the count of each character
                char_count = {'(': 0, ')': 0, '*': 0}
                for c in substring:
                    if c in char_count:
                        char_count[c] += 1

                # Initialize a stack to keep track of the opening parentheses
                stack = []

                # Initialize a flag to indicate if the substring is valid
                is_valid = True

                for c in substring:
                    if c == '(':
                        stack.append(c)
                    elif c == ')':
                        if not stack:
                            is_valid = False
                            break
                        else:
                            stack.pop()
                    elif c == '*':
                        if stack:
                            stack.pop()
                        else:
                            stack.append(c)

                # If the stack is not empty, the substring is not valid
                if stack:
                    is_valid = False

                # If the substring is valid, add it to the list of valid substrings
                if is_valid:
                    valid_substrings.append(substring)

        # Create a list to store the reversed valid substrings
        reversed_valid_substrings = []

        for substring in valid_substrings:
            # Create a dictionary to store the count of each character
            char_count = {'(': 0, ')': 0, '*': 0}
            for c in substring:
                if c in char_count:
                    char_count[c] += 1

            # Initialize a stack to keep track of the opening parentheses
            stack = []

            # Initialize a flag to indicate if the substring is valid
            is_valid = True

            for c in substring[::-1]:
                if c == '(':
                    stack.append(c)
                elif c == ')':
                    if not stack:
                        is_valid = False
                        break
                    else:
                        stack.pop()
                elif c == '*':
                    if stack:
                        stack.pop()
                    else:
                        stack.append(c)

            # If the stack is not empty, the substring is not valid
            if stack:
                is_valid = False

            # If the substring is valid, add it to the list of valid substrings
            if is_valid:
                reversed_valid_substrings.append(substring[::-1])

        # Return True if there are any valid substrings, False otherwise
        return len(valid_substrings) or len(reversed_valid_substrings)

