class Solution:
    def checkValidString(self, s: str) -> bool:
        l = [0] * (len(s) + 1)
        for i, c in enumerate(s, 1):
            l[i] = l[i - 1]
            if c == '(':
                l[i] += 1
            elif c == ')':
                if l[i] == 0:
                    return False
                l[i] -= 1
            elif l[i] > 0:
                l[i] -= 1
        return l[-1] <= 0

