class Solution:
    def checkValidString(self, s: str) -> bool:
        lo, hi = 0, 0
        for c in s:
            lo += 1 if c == '(' else -1
            hi += 1 if c != ')' else -1
            if lo < 0:
                lo = 0
                hi = 0
            elif hi < 0:
                return False
        return lo == 0
