inefficient_solution:
class Solution:
    def mySqrt(self, x: int) -> int:
        if x == 0 or x == 1:
            return x
        sqrt_values = [0] * (x + 1)
        for i in range(1, x + 1):
            possible_sqrt = i
            for j in range(1, i):
                if j * j > i:
                    break
                if j * j == i:
                    possible_sqrt = j
                    break
            sqrt_values[i] = possible_sqrt
        return sqrt_values[x]

canonical_solution:
class Solution:
    def mySqrt(self, x: int) -> int:
        left, right = 0, x
        while left < right:
            mid = (left + right + 1) >> 1
            # mid*mid <= x
            if mid <= x // mid:
                left = mid
            else:
                right = mid - 1
        return left

