class Solution:
    def canPartitionKSubsets(self, nums, k):
        total = sum(nums)
        if total % k != 0:
            return False
        target = total // k
        nums.sort(reverse=True)
        visited = {i: False for i in range(len(nums))}

        def dfs(index):
            if index == len(nums):
                return True
            for i in range(k):
                if not visited[i]:
                    if index == len(nums) - 1:
                        return dfs(index + 1)
                    if nums[index] > target:
                        continue
                    if i > 0 and nums[index] + target != nums[index - 1]:
                        continue
                    visited[i] = True
                    if dfs(index + 1):
                        return True
                    visited[i] = False
            return False

        return dfs(0)
