class Solution:
    def advantageCount(self, nums1: List[int], nums2: List[int]) -> List[int]:
        nums1.sort()
        nums2.sort()
        m, n = len(nums1), len(nums2)
        ans = [0] * n
        i = j = 0
        while j < n:
            if nums1[i] <= nums2[j]:
                j += 1
                if j < n:
                    ans[nums2[j - 1]] = nums1[m - 1]
                    m -= 1
                else:
                    break
            else:
                ans[nums2[j]] = nums1[i]
                i += 1
                j += 1
        if i < m:
            ans[nums2[n - 1]] = nums1[i:]
        return ans
