    def lemonadeChange(self, bills: List[int]) -> bool:
        total_five = [0] * 1000000
        total_ten = [0] * 1000000
        for i, v in enumerate(bills):
            if v == 5:
                total_five[i] = 1
            elif v == 10:
                total_ten[i] = 1
                if total_five[i-1] == 1:
                    total_five[i-1] = 0
                elif total_five[i-2] == 1:
                    total_five[i-2] = 0
            else:
                if total_ten[i-1] == 1:
                    total_ten[i-1] = 0
                    if total_five[i-1] == 1:
                        total_five[i-1] = 0
                    elif total_five[i-2] == 1:
                        total_five[i-2] = 0
                else:
                    for j in range(i-1, -1, -1):
                        if total_five[j] == 1:
                            total_five[j] = 0
                            break
                if total_five[i-1] == 1:
                    total_five[i-1] = 0
                elif total_five[i-2] == 1:
                    total_five[i-2] = 0
                elif total_five[i-3] == 1:
                    total_five[i-3] = 0
                elif total_five[i-4] == 1:
                    total_five[i-4] = 0
                elif total_five[i-5] == 1:
                    total_five[i-5] = 0
                else:
                    return False
            if total_five[i] < 0:
                return False
        return True

canonical_solution:
class Solution:
    def lemonadeChange(self, bills: List[int]) -> bool:
        five = ten = 0
        for v in bills:
            if v == 5:
                five += 1
            elif v == 10:
                ten += 1
                five -= 1
            else:
                if ten:
                    ten -= 1
                    five -= 1
                else:
                    five -= 3
            if five < 0:
                return False
        return True
