                elif i > j and i < n and j >= n:
                    f[i][j] = 1
                elif i > j and i >= n and j < n:
                    f[i][j] = 1
                elif i > j and i >= n and j >= n:
                    f[i][j] = 2*n
                elif i == j and i >= n:
                    f[i][j] = 1
                elif i == j and i < n:
                    f[i][j] = 0

        for k in range(1, 2*n):
            for i in range(1, 2*n):
                for j in range(1, 2*n - i + 1):
                    if i + j + k == 2*n:
                        f[i][j+k] = 0
                    else:
                        f[i][j+k] = 2*n

        for i in range(1, 2*n):
            for j in range(1, 2*n):
                if i < 2*n and j < 2*n:
