class Solution:
    def countTriplets(self, arr: List[int]) -> int:
        n = len(arr)
        pre = [0] * (n + 1)
        for i in range(n):
            pre[i + 1] = pre[i] ^ arr[i]
        ans = 0
        for i in range(n - 1):
            for j in range(i + 1, n):
                a = pre[i] ^ pre[j]
                ok = False
                for k in range(j, n):
                    if pre[j] == pre[k]:
                        ok = True
                        break
                    a ^= arr[k]
                if ok:
                    ans += 1
        return ans
