class Solution:
    def hasAllCodes(self, s: str, k: int) -> bool:
        codes = []
        for i in range(len(s) - k + 1):
            codes.append([int(c) for c in s[i : i + k]])
        return len(set(tuple(code) for code in codes)) == 1 << k

efficient_solution:
class Solution:
    def hasAllCodes(self, s: str, k: int) -> bool:
        ss = {s[i : i + k] for i in range(len(s) - k + 1)}
        return len(ss) == 1 << k

model_solution:
class Solution:
    def hasAllCodes(self, s: str, k: int) -> bool:
        t = ord('1')
        ans = set()
        for i, c in enumerate(s):
            if ord(c) == t:
                ans.add(i - k + 1)
        return len(ans) == 1 << k
