        x = sum(nums) - x
        res = []
        n = len(nums)
        for i in range(n):
            temp = nums[i:]
            temp_sum = sum(temp)
            if temp_sum == x:
                res.append(temp)
        if not res:
            return -1
        ans = inf
        for i, v in enumerate(res):
            ans = min(ans, n - len(v))
        return ans

