class Solution:
    def sortFeatures(self, features: List[str], responses: List[str]) -> List[str]:
        dict = {}
        for i, feature in enumerate(features):
            dict[feature] = []
        for response in responses:
            words = response.split()
            for feature in features:
                if feature in words:
                    dict[feature].append(response)
        cnt = {feature: len(set(response for response in responses for word in response.split() if word == feature)) for feature in features}
        return sorted(features, key=lambda w: (-cnt[w], features.index(w)))

