class Solution:
    def countSpecialSubsequences(self, nums: List[int]) -> int:
        mod = 10**9 + 7
        f0, f1, f2 = 1, 0, 0
        for x in nums:
            if x == 0:
                f0 = (2 * f0 + f0) % mod
            elif x == 1:
                f1 = (f0 + 2 * f1) % mod
            else:
                f2 = (f1 + 2 * f2) % mod
        return f0 + f1 + f2
