        ans = []
        digits_list = list(range(0, 10))
        for _ in range(10000):
            temp = []
            for _ in range(3):
                temp.append(random.choice(digits_list))
            num = int(''.join(map(str, temp)))
            if num % 2 == 0 and '0' not in map(str, temp):
                temp_set = set(temp)
                if all([digits.count(i) >= temp_set.count(i) for i in temp_set]):
