class Solution:
    def beautifulPartitions(self, s: str, k: int, minLength: int) -> int:
        primes = '2357'
        if s[0] not in primes or s[-1] in primes:
            return 0
        mod = 10**9 + 7
        n = len(s)
        f = [1] + [0] * n
        g = [0] * (n + 1)
        for i, c in enumerate(s, 1):
            for j in range(1, k + 1):
                if i - minLength + 1 <= j:
                    f[j] = (f[j] + g[i - minLength] * f[j - 1]) % mod
            for j in range(k + 1):
                g[i] = (g[i - 1] + f[j]) % mod
        return f[k]
