                if s[j] == "*":
                    if i == j:
                        dp_cnt[i + 1] += 9
                    else:
                        dp_cnt[i + 1] += 15
                elif s[j] != "0":
                    if i == j:
                        dp_cnt[i + 1] += 1
                    else:
                        dp_cnt[i + 1] += 1
                if i > 0:
                    if s[i - 1] == "*" and s[j] == "*":
                        dp_cnt[i + 1] = (dp_cnt[i + 1] + 15 * dp_cnt[i]) % mod
                        if (j - i + 1) >= 2:
                            dp[j - i + 2] = (dp_cnt[i] * 15) % mod
                    elif s[i - 1] == "*":
                        if s[j] > "6":
                            dp_cnt[i + 1] = (dp_cnt[i + 1] + dp_cnt[i]) % mod
                        else:
                            dp_cnt[i + 1] = (dp_cnt[i + 1] + 2 * dp_cnt[i]) % mod
                            dp[j - i + 2] = (dp_cnt[i] * 2) % mod
