inefficient_solution:
class Solution:
    def pyramidTransition(self, bottom: str, allowed: List[str]) -> bool:
        d = {a: {} for a in bottom}
        for a, b, c in allowed:
            if a not in d:
                d[a] = {}
            if b not in d[a]:
                d[a][b] = []
            d[a][b].append(c)

        def dfs(s):
            if len(s) == 1:
                return True
            t = []
            for i in range(len(s) - 1):
                a, b = s[i], s[i + 1]
                if a not in d or b not in d[a]:
                    return False
                cs = d[a][b]
                if len(cs) == 0:
                    return False
                t.append(cs)
            return any(dfs(''.join(nxt)) for nxt in itertools.product(*t))

        return dfs(bottom)

efficient_solution:
class Solution:
    def pyramidTransition(self, bottom: str, allowed: List[str]) -> bool:
        @cache
        def dfs(s):
            if len(s) == 1:
                return True
            t = []
            for a, b in pairwise(s):
                cs = d[a, b]
                if not cs:
                    return False
                t.append(cs)
            return any(dfs(''.join(nxt)) for nxt in product(*t))

        d = defaultdict(list)
        for a, b, c in allowed:
            d[a, b].append(c)
        return dfs(bottom)

